mapper.select:
select MEM.MEMBER_SEQ as MEMBER_SEQ , MEM.EMAIL as EMAIL , MEM.NAME as NAME , MEM.PHONE as PHONE , AUTH.AUTHORITY as AUTHORITY
from MEMBER MEM INNER JOIN AUTHORITY_REL AUTH_REL ON MEM.MEMBER_SEQ = AUTH_REL.MEMBER_SEQ INNER JOIN AUTHORITY AUTH ON AUTH_REL.ID_AUTHORITY = AUTH.ID_AUTHORITY
and 1=1
and MEM.EMAIL = #{SEARCH_KEYWORD}
and MEM.NAME = #{SEARCH_KEYWORD}
and 1=1

mapper.insert:

MERGE INTO MEMBER M1 
     USING (SELECT #{MEMBER_SEQ} AS MEMBER_SEQ FROM DUAL) M2
		ON (M1.MEMBER_SEQ = M2.MEMBER_SEQ)
WHEN MATCHED THEN 
	UPDATE SET
		EMAIL		= #{EMAIL}
		, NAME		= #{NAME}
		, PHONE		= #{PHONE}
		, PASSWORD	= #{PASSWORD}
WHEN NOT MATCHED THEN 
	INSERT(
		MEMBER_SEQ	
		, EMAIL
		, NAME
		, PHONE
		, PASSWORD
	)
	values(
		#{MEMBER_SEQ}
		, #{EMAIL}
		, #{NAME}
		, #{PHONE}
		, #{PASSWORD}
	)
